mapper.insert:
INSERT INTO Board (
    Title, Content, CreationDate, ContentImg, Category, MemberId
) VALUES (
    #{Title}, #{Content}, #{CreationDate}, #{ContentImg}, #{Category}, #{MemberId}
)

mapper.update:
UPDATE Board SET
    Title=#{Title}, Content=#{Content}, CreationDate=#{CreationDate},
    ContentImg=#{ContentImg}, Category=#{Category}, MemberId=#{MemberId}
WHERE BoardId=#{BoardId}
